get '/stopbyid' do
  content_type :json

  gotime = Time.now() - 60 * 60 * 4

  stations = ""
  if(gotime.wday == 0)
    stations = [ ]
  elsif(gotime.wday == 6)
    # saturday schedule
    stations = get_saturday_stations()
  else
    # weekday schedule
    stations = get_stations()
  end
  closestStations = [ ]
  stations.each do |station|
    if(station.getid() == params['id'])
      closestStations.push(station)
    end
  end

  closest = closestStations[0]
  routePrintOut = [ ]
  closestStations.each do |station|
    intime = "NONE"
    station.getintimes().each do |time|
      turntime = time.split(":")
      if(turntime[0].to_i * 60 + turntime[1].to_i > gotime.hour * 60 + gotime.min)
        intime = time
        break
      end
    end
    outtime = "NONE"
    station.getouttimes().each do |time|
      turntime = time.split(":")
      if(turntime[0].to_i * 60 + turntime[1].to_i > gotime.hour * 60 + gotime.min)
        outtime = time
        break
      end
    end
    routePrintOut.push('{ "route": "' + station.getroute()[0] + '", "intime": "' + intime + '", "outtime": "' + outtime + '"}');
  end
  if(params['jsonp'])
    return params['jsonp'] + '({ "id": "' + closest.getid() + '", "name":"' + closest.getname() + '", "routes": [ "' + closest.getroute().join('","') + '" ], "latlng": [ ' + closest.getlat().to_s + ',' + closest.getlng().to_s + ' ], "routes": [ ' + routePrintOut.join(',') + ' ] });'
  else
    return '{ "id": "' + closest.getid() + '", "name":"' + closest.getname() + '", "routes": [ "' + closest.getroute().join('","') + '" ], "latlng": [ ' + closest.getlat().to_s + ',' + closest.getlng().to_s + ' ], "routes": [ ' + routePrintOut.join(',') + ' ] }'
  end

end

def closest_macon(lat, lng, weekday)
  stations = ""
  if(weekday == 6)
    # saturday schedule
    stations = get_saturday_stations()
  else
    # weekday schedule
    stations = get_stations()
  end
  closestStation = nil
  closestDistance = 100000
  closestStationVersions = [ ]
  stations.each do |station|
    # if it's Saturday, don't count stops where bus is not running on Saturday
    if(weekday == 6 and station.getroute()[0].split("-")[0] == "1")
      next
    end
    # LIBRARY FIX: can't get to library on weekdays via Route 13
    if(weekday < 6 and station.getroute()[0].split("-")[0] == "13")
      next
    end
    dist = (station.getlat() - lat )**2 + ( station.getlng() - lng )**2
    if(dist < closestDistance)
      closestDistance = dist
      closestStationVersions = [ station ]
    elsif(dist == closestDistance and closestStationVersions[0].getid() == station.getid() )
      closestStationVersions.push(station)
    end
  end
  return closestStationVersions
end

