get '/routeit' do
  if(params['address'])
    # load the event and its timestamp
    event = TransitEvent.first( :eventname => params['eventname'] )

    day = event.dateof.split('/')[1]
    month = event.dateof.split('/')[0]
    year = event.dateof.split('/')[2]   
    hour = event.timeof.split(':')[0]
    if event.ampm and event.ampm == "pm"
      if hour.to_i < 12
        hour = hour.to_i + 12
        hour = hour.to_s
      end
    elsif hour == "12" and event.ampm and event.ampm == "am"
      hour = "0"
    end
    minute = event.timeof.split(':')[1]
    gotime = Time.new( year, month, day, hour, minute, 0, "-04:00" )

    # No Sunday buses
    if gotime.wday == 0
      return "<!DOCTYPE html>\n<html><body style='font-family:arial'>No Sunday buses</body></html>"
    end

    # convert user's address to their nearest bus stop active today
    address = params['address']
    if(address.downcase.index('macon') == nil)
      address += ",Macon,GA"
    end
    url = 'http://geocoder.us/service/csv/geocode?address=' + URI.escape(address)
    url = URI.parse(url)
    res = Net::HTTP.start(url.host, url.port) {|http|
      http.get('/service/csv/geocode?address=' + URI.escape(address))
    }
    response = res.body.split(",")
    lat = Float( response[0] )
    lng = Float( response[1] )
    closest = closest_macon(lat, lng, gotime.wday)[0]

    # convert event address to its nearest bus stop
    # gotostation is stored as ADDRESS, Macon, GA|LATITUDE|LONGITUDE
    lat = event.gotostation.split("|")[ event.gotostation.split("|").length-2 ]
    lng = event.gotostation.split("|")[ event.gotostation.split("|").length-1 ]
    gostation = closest_macon(lat.to_f, lng.to_f, gotime.wday)[0]

    bussum = ''
    turntime = [ ]
    endtime = [ ]
    firsttime = [ ]

    busout = ""

    # look for routes shared between the start and end stops
    sameroutes = nil
    closest.getroute().each do |rt|
      if gostation.hasroute(rt.split('-')[0])
        # prevent invalid route-day matches ( in this case, Rt 1 on a Saturday ) 
        if (rt != "1" or gotime.wday < 6)
          sameroutes = rt
          break
        end
      end
    end

    if sameroutes != nil
      # possible to take the same bus route
      # get schedule
      sendMeOutbound = 0 # for stops only going outbound
      if sameroutes == "1"
        if gotime.wday < 6
          # Weekday schedule
          sched = getSchedule("1-W")
        end
        busout += "<h3>" + address + "</h3>" + bussum + "<br/>Take bus (1) from <i>" + closest.getname() + "</i>."

      elsif sameroutes == "2"
        routes = closest.getroute()
        if routes.index("2") != nil or routes.index("2-I") != nil or routes.index("2-O") != nil
          if gotime.wday == 6
            # Saturday schedule
            sched = getSchedule("2-S")
          else
            # Weekday schedule
            sched = getSchedule("2-W")
          end
        elsif gotime.wday == 6 or gotime.hour < 6 or gotime.hour > 18
          # Route 2B
          if gotime.wday == 6
            # Saturday schedule
            sched = getSchedule("2-S")
          else
            # Weekday schedule
            sched = getSchedule("2-W")
          end
        end
        busout += "<h3>" + address + "</h3>" + bussum + "<br/>Take bus (2) from <i>" + closest.getname() + "</i>."

      elsif sameroutes == "3"
        if gotime.wday == 6
          # Saturday schedule
          sched = getSchedule("3-S")
        else
          # weekday schedule
          sched = getSchedule("3-W")
        end
        if closest.hasroute("3-O")
          sendMeOutbound = 1
        end
        busout += "<h3>" + address + "</h3>" + bussum + "<br/>Take bus (3) from <i>" + closest.getname() + "</i>."

      elsif sameroutes == "4"
        if gotime.wday == 6
          sched = getSchedule("4-S")
        else
          sched = getSchedule("4-W")
        end
        if closest.hasroute("4-O")
          sendMeOutbound = 1
        end
        busout += "<h3>" + address + "</h3>" + bussum + "<br/>Take bus (4) from <i>" + closest.getname() + "</i>."

      elsif sameroutes == "5"
        if gotime.wday == 6
          # Saturday schedule
          sched = getSchedule("5-S")
        else
          # weekday schedule
          sched = getSchedule("5-W")
        end
        if closest.hasroute("5-O")
          sendMeOutbound = 1
        end
        busout += "<h3>" + address + "</h3>" + bussum + "<br/>Take bus (5) from <i>" + closest.getname() + "</i>."
            
      elsif sameroutes == "6"
        if gotime.wday == 6
