          else
            sched = getSchedule("4-W")
          end
          busout += bussum + "<br/>Take bus (4) to <i>" + gostation.getname() + "</i> from Terminal Station."

        elsif gostation.hasroute("5")
          if gotime.wday == 6
            # Saturday schedule
