          else
            # weekday schedule
            sched = getSchedule("9-W")
          end
          busout += bussum + "<br/>Take bus (9) to <i>" + gostation.getname() + "</i> from Terminal Station."

        elsif gostation.hasroute("11")
          if gotime.wday == 6
            # Saturday schedule
